import React, { useEffect, useState } from 'react';
import * as constant from"../Assets/Constants"
import './Home.css';


function Home_Page() {

    const[vyaktiList,setVyaktiList] = useState([]);
    const[selectedVyakti,setselectedVyakti] = useState({});
    const[vyakti,setVyakti] = useState({
        name : '',
        fatherName : '',
        isLiving : true,
        gender : '',
        maritalStatus : '',
        isFirst : false,
        nearestRelative : {},
        relationship : '',
        isDeletable : true,
        remarks : ''
    });

    const getVanshawali = () => {
        try {
            fetch(constant.getVanshawali)
                .then((response) => {
                    // console.log(response);
                    return response.json();
                })
                .then((myJson) => {
                    // console.log(myJson);
                    setVyaktiList(myJson);
                });
        } catch (e) {
            console.log(e);
        }
    };
    useEffect(() => {
        getVanshawali();
    }, []);

    const handleSelectedVyakti = (e) => {

        setselectedVyakti(e.target.value);
        console.log(selectedVyakti);

    };

    const handleIsLiving = (e) => {

        if(e.target.value==='हाँ')
        {
            setVyakti({
                ...vyakti,
                isLiving : true
            });
        }
        else{
            setVyakti({
                ...vyakti,
                isLiving : false
            });
        }

    };

    const handleInput = (e,prop) => {

        setVyakti({
            ...vyakti,
            [prop] : e.target.value
        });

    }

    const handleSubmit = () => {
        console.log(vyakti);
    } 

    const handleGender = (e) => {

        if(e.target.value==='पुरुष')
        {
            setVyakti({
                ...vyakti,
                gender : 'Male'
            });
        }
        else{
            setVyakti({
                ...vyakti,
                gender : 'Female'
            });
        }

    };

    const handleMaritalStatus = (e) => {

        if(e.target.value==='विवाहित')
        {
            setVyakti({
                ...vyakti,
                maritalStatus : 'Married'
            });
        }
        else{
            setVyakti({
                ...vyakti,
                maritalStatus : 'Unmarried'
            });
        }

    };

    return(
       <div>
<header>Data Entry Page</header>

<div className='dropdownBox'>
    <select id="dropdown"
    value={selectedVyakti}
     onChange={handleSelectedVyakti}
    >
        <option value=" ">व्यक्ति चुनें</option>
        {vyaktiList &&
                        vyaktiList.length > 0 &&
                        vyaktiList.map((gname, key) => {
                            return (
                                <option key={gname.vyaktiId}> {gname.name+" पिता - "+gname.fatherName}</option>
                            );
                        })}

    </select>
</div>

<div className='DataBox'>
<label htmlFor="name">व्यक्ति का नाम</label>    
<input name='name' type="text" className="inputData" placeholder='व्यक्ति का नाम' onChange={e=> handleInput(e,'name')} /><br />

<label htmlFor="fatherName">पिता का नाम</label>   
<input type="text" name='fatherName' placeholder='व्यक्ति के पिता का नाम' className="inputData" onChange={e=> handleInput(e,'fatherName')} /><br />    

<label htmlFor="living">जीवित हैं ?</label>   
<select name="living" className="inputData" placeholder='जीवित हैं ?' onChange={handleIsLiving} >
    <option selected="true">हाँ</option>
    <option>नहीं</option>
</select><br />

<label htmlFor="gender">लिंग</label>   
<select name="gender" className="inputData" placeholder='जीवित हैं ?' onChange={handleGender} >
    <option selected="true">पुरुष</option>
    <option>स्त्री</option>
</select><br />

<label htmlFor="marital">वैवाहिक स्थिति</label>   
<select name="marital" className="inputData" placeholder='जीवित हैं ?' onChange={handleMaritalStatus} >
    <option selected="true">विवाहित</option>
    <option>अविवाहित</option>
</select><br />

<label htmlFor="nearestRelative">निकटतम संबंधी</label>   
<select name="nearestRelative" className="inputData"  >
<option value=" ">व्यक्ति चुनें</option>
        {vyaktiList &&
                        vyaktiList.length > 0 &&
                        vyaktiList.filter((person) => 
                        person.name !== vyakti.name
                        ).map((gname, key) => {
                            return (
                                <option key={gname.vyaktiId}> {gname.name+" पिता - "+gname.fatherName}</option>
                            );
                        })}
</select><br />

<label htmlFor="relationship">संबंधी,इस व्यक्ति के ?</label>   
<select name="relationship" className="inputData" placeholder='जीवित हैं ?' onChange={handleRelationship} >
    <option selected="true">पिता</option>
    <option>भाई-बहन</option>
</select><br />

<div className='buttonSet'>
    <button className="bottomBtn" class="btn btn-warning" onClick={handleSubmit}>Save</button>


</div>

</div>

       </div>
    )
};

export default Home_Page;
