import React, { useEffect, useState } from 'react';
import * as constant from"../Assets/Constants"
import './Home.css';


function Home_Page() {

    const[vyaktiList,setVyaktiList] = useState([]);
    const[selectedVyakti,setselectedVyakti] = useState({});
    const[vyakti,setVyakti] = useState({
        name : '',
        fatherName : '',
        isLiving : true,
        gender : '',
        maritalStatus : '',
        isFirst : false,
        nearestRelative : {},
        relationship : '',
        isDeletable : true,
        remarks : ''
    });

    const getVanshawali = () => {
        try {
            fetch(constant.getVanshawali)
                .then((response) => {
                    // console.log(response);
                    return response.json();
                })
                .then((myJson) => {
                    // console.log(myJson);
                    setVyaktiList(myJson);
                });
        } catch (e) {
            console.log(e);
        }
    };
    useEffect(() => {
        getVanshawali();
    }, []);

    useEffect(() => {
        setCurrentVyakti();
    },{});

    const setCurrentVyakti =() => {

        setVyakti(vyakti);
    }; 

    const handleSelectedVyakti = (e) => {

        setselectedVyakti(e.target.value);
        console.log(selectedVyakti);

    };

    const handleIsLiving = (e) => {

        if(e.target.value==='हाँ')
        {
            setVyakti({
                ...vyakti,
                isLiving : true
            });
        }
        else{
            setVyakti({
                ...vyakti,
                isLiving : false
            });
        }
        setCurrentVyakti();
    };

    const handleInput = (e,prop) => {

        setVyakti({
            ...vyakti,
            [prop] : e.target.value
        });

    }

    const handleSubmit = () => {
        console.log(JSON.stringify(vyakti));



       var url = constant.addVyakti; 
       fetch(url, {
        method : "Post",
        body : JSON.stringify(vyakti)
       })
       .then((response) => console.log(response));
    } 

    const handleGender = (e) => {

        if(e.target.value==='पुरुष')
        {
            setVyakti({
                ...vyakti,
                gender : 'Male'
            });
        }
        else{
            setVyakti({
                ...vyakti,
                gender : 'Female'
            });
        }

    };

    const handleMaritalStatus = (e) => {

        console.log("Marital status -= "+e.target.value);

        if(e.target.value==='विवाहित')
        {
            setVyakti({
                ...vyakti,
                maritalStatus : 'Married'
            });
        }
        else{
            setVyakti({
                ...vyakti,
                maritalStatus : 'Unmarried'
            });
        }

    };

    const handleRelationship = (e) => {

        if(e.target.value==='पिता')
        {
            setVyakti({
                ...vyakti,
                relationship : 'Father'
            });

        }
        else{
            setVyakti({
                ...vyakti,
                relationship : 'Sibling'
            });           
        }

    };

    return(
       <div>
<header>Data Entry Page</header>

<div className='dropdownBox'>
    <select id="dropdown"
    value={selectedVyakti}
     onChange={handleSelectedVyakti}
    >
        <option value=" ">व्यक्ति चुनें</option>
        {vyaktiList &&
                        vyaktiList.length > 0 &&
                        vyaktiList.map((gname, key) => {
                            return (
                                <option key={gname.vyaktiId}> {gname.name+" पिता - "+gname.fatherName}</option>
                            );
                        })}

    </select>
</div>

<div className='DataBox'>
<label htmlFor="name" className='labelText'>व्यक्ति का नाम</label>    
<input name='name' type="text" className="inputData" placeholder='व्यक्ति का नाम' onChange={e=> handleInput(e,'name')} /><br />

<label htmlFor="fatherName" className='labelText'>पिता का नाम</label>   
<input type="text" name='fatherName' placeholder='व्यक्ति के पिता का नाम' className="inputData" onChange={e=> handleInput(e,'fatherName')} /><br />    

<label htmlFor="living" className='labelText'>जीवित हैं ?</label>   
<select name="living" value={vyakti.isLiving} className="inputData" placeholder='जीवित हैं ?' onChange={e=> handleInput(e,'isLiving')} >
<option disabled selected value={""}>जीवित हैं ?</option>
    <option value={true} >हाँ</option>
    <option value={false}>नहीं</option>
</select><br />

<label htmlFor="gender" className='labelText'>लिंग</label>   
<select name="gender" className="inputData" onChange={handleGender} >
<option>लिंग चुनें</option>
    <option >पुरुष</option>
    <option>स्त्री</option>
</select><br />

<label htmlFor="marital" className='labelText'>वैवाहिक स्थिति</label>   
<select name="marital" className="inputData" onChange={handleMaritalStatus} >
    <option>वैवाहिक स्थिति ?</option>
    <option >विवाहित</option>
    <option>अविवाहित</option>
</select><br />

<label htmlFor="nearestRelative" className='labelText'>निकटतम संबंधी</label>   
<select name="nearestRelative" className="inputData"  >
<option value=" ">व्यक्ति चुनें</option>
        {vyaktiList &&
                        vyaktiList.length > 0 &&
                        vyaktiList.filter((person) => 
                        person.name !== vyakti.name
                        ).map((gname, key) => {
                            return (
                                <option key={gname.vyaktiId}> {gname.name+" पिता - "+gname.fatherName}</option>
                            );
                        })}
</select><br />

<label htmlFor="relationship" className='labelText'>संबंधी,इस व्यक्ति के ?</label>   
<select name="relationship" value={vyakti.relationship} className="inputData" placeholder='जीवित हैं ?' onChange={handleRelationship} >
    <option>पिता</option>
    <option>भाई-बहन</option>
</select><br />

<label htmlFor="remarks" id="remarks" className='labelText'>कोई अन्य जानकारी</label>   
<textarea name='remarks' className="inputData" placeholder='कुछ अन्य जानकारी' onChange={e=> handleInput(e,'remarks')} /><br />
