import React, { useEffect, useState } from 'react';
import * as constant from"../Assets/Constants"
import './Home.css';
import { Button } from 'bootstrap';

function Home_Page() {

    const[vyaktiList,setVyaktiList] = useState([]);
    const[selectedVyakti,setselectedVyakti] = useState({});
    const[vyakti,setVyakti] = useState({
        name : '',
        fatherName : '',
        isLiving : true,
        gender : '',
        maritalStatus : '',
        isFirst : false,
        nearestRelative : '',
        relationship : '',
        isDeletable : true,
        remarks : ''
    });
    const[vyaktiName,setVyaktiName] = useState("");
    const[vyaktiFatherName,setVyaktiFatherName] = useState("");


    const getVanshawali = () => {
        try {
            fetch(constant.getVanshawali)
                .then((response) => {
                    // console.log(response);
                    return response.json();
                })
                .then((myJson) => {
                    // console.log(myJson);
                    setVyaktiList(myJson);
                });
        } catch (e) {
            console.log(e);
        }
    };
    useEffect(() => {
        getVanshawali();
    }, []);

    const handleSelectedVyakti = (e) => {

        setselectedVyakti(e.target.value);
        console.log(selectedVyakti);

    };

    const handleIsLiving = (e) => {

        if(e.target.value==='हाँ')
        {
            setVyakti({
                ...vyakti,
                isLiving : true
            });
        }
        else{
            setVyakti({
                ...vyakti,
                isLiving : false
            });
        }

    };

    const handleInput = (e,prop) => {

        setVyakti({
            ...vyakti,
            [prop] : e.target.value
        });

    }

    const handleSubmit = () => {
        console.log(vyakti);
    } 

    return(
       <div>
<header>Data Entry Page</header>

<div className='dropdownBox'>
    <select id="dropdown"
    value={selectedVyakti}
     onChange={handleSelectedVyakti}
    >
        <option value=" ">व्यक्ति चुनें</option>
        {vyaktiList &&
                        vyaktiList.length > 0 &&
                        vyaktiList.map((gname, key) => {
                            return (
                                <option key={gname.vyaktiId}> {gname.name+" पिता - "+gname.fatherName}</option>
                            );
                        })}

    </select>
</div>

<div className='DataBox'>
<label htmlFor="name">व्यक्ति का नाम</label>    
<input name='name' type="text" className="inputData" placeholder='व्यक्ति का नाम' onChange={e=> handleInput(e,'name')} /><br />

<label htmlFor="fatherName">पिता का नाम</label>   
<input type="text" name='fatherName' placeholder='व्यक्ति के पिता का नाम' className="inputData" onChange={e=> handleInput(e,'fatherName')} /><br />    

<label htmlFor="living">जीवित हैं ?</label>   
<select name="living" className="inputData" placeholder='जीवित हैं ?' onChange={handleIsLiving} >
    <option>हाँ</option>
    <option>नहीं</option>
</select>

<div className='buttonSet'>
    <button className="labelText" class="btn btn-warning" onClick={handleSubmit}>Save</button>


</div>

</div>

       </div>
    )
};

export default Home_Page;
